SELECT COUNT(name_in_edition) AS 'Total Number of Correspondents who wrote letters to and received letters by Budé' FROM correspondents WHERE name_in_edition NOT IN (SELECT name_in_edition AS 'Correspondents who wrote to but did not receive any letters by Budé' FROM correspondents WHERE correspondents.correspondents_id NOT IN (SELECT DISTINCT recipient_id FROM letters WHERE recipient_id != 'budé_guillaume_viaf_105878228') GROUP BY name_in_edition) AND name_in_edition NOT IN (SELECT name_in_edition FROM correspondents WHERE correspondents.correspondents_id NOT IN (SELECT DISTINCT sender_id FROM letters WHERE sender_id != 'budé_guillaume_viaf_105878228') GROUP BY name_in_edition)
