SELECT COUNT(locations.locations_name_modern) AS 'Number of locations from where Budé has both written and received letters from' FROM locations WHERE locations.locations_id IN (SELECT DISTINCT source_loc_id FROM letters WHERE sender_id = 'budé_guillaume_viaf_105878228' AND source_loc_id NOT LIKE 'unknown%' GROUP BY source_loc_id) AND locations.locations_id IN (SELECT DISTINCT source_loc_id FROM letters WHERE recipient_id = 'budé_guillaume_viaf_105878228' AND source_loc_id NOT LIKE 'unknown%' GROUP BY source_loc_id)
