SELECT x.recipient_id AS Correspondent, LEAST(x.FLFE,x.LLFE,y.FLTE,y.LLTE) AS 'Start of Correspondence', GREATEST(x.FLFE,x.LLFE,y.FLTE,y.LLTE) AS 'End of Correspondence' FROM (SELECT DISTINCT recipient_id, MIN(send_date_computable1) AS FLFE, MAX(send_date_computable1) AS LLFE FROM letters WHERE sender_id = 'budé_guillaume_viaf_105878228' GROUP BY recipient_id) AS X
INNER JOIN (SELECT DISTINCT sender_id, MIN(send_date_computable1) AS FLTE, MAX(send_date_computable1) AS LLTE FROM letters WHERE recipient_id = 'budé_guillaume_viaf_105878228' GROUP BY sender_id) AS Y ON X.recipient_id = Y.sender_id
