SELECT COUNT(*) AS 'Number of Correspondents that both wrote to and received letters from Budé' FROM budé_cdb_v1.correspondents WHERE correspondents.correspondents_id IN (SELECT DISTINCT recipient_id FROM letters WHERE recipient_id != 'budé_guillaume_viaf_105878228') AND correspondents.correspondents_id IN (SELECT sender_id FROM letters WHERE sender_id != 'budé_guillaume_viaf_105878228')
