SELECT locations_name_modern AS 'Location Name Modern', locations_lat AS Latitude, locations_lng AS Longitude, COUNT(DISTINCT sender_id) AS 'Number of Correspondents who wrote from this location TO Erasmus' FROM letters JOIN era_cdb_v3.locations ON locations.locations_id = letters.source_loc_id WHERE recipient_id = 'erasmus_desiderius_viaf_95982394' GROUP BY source_loc_id ORDER BY COUNT(DISTINCT sender_id) DESC
