SELECT locations.locations_name_modern AS 'Location', locations.locations_lat AS 'Latitude', locations.locations_lng as 'Longitude', COUNT(letters.target_loc_id) AS 'Number of Letters per Location to which Erasmus has sent Letters' FROM letters JOIN locations ON locations.locations_id = letters.target_loc_id WHERE letters_id NOT LIKE '%ck2' AND sender_id = 'erasmus_desiderius_viaf_95982394' AND target_loc_id NOT LIKE 'unknown%' GROUP BY target_loc_id ORDER BY COUNT(letters.target_loc_id) DESC
