SELECT COUNT(*) AS 'Total number of letters exchanged between Budé and mutual correspondents of his and and Erasmus' FROM (SELECT * from budé_cdb_v1.letters) AS L WHERE L.sender_id IN (SELECT X.correspondents_id FROM budé_cdb_v1.correspondents AS X WHERE X.correspondents_id IN (Select E.correspondents_id from era_cdb_v3.correspondents AS E, budé_cdb_v1.correspondents AS B WHERE E.correspondents_id = B.correspondents_id AND E.correspondents_id NOT LIKE 'unnamed_person_viaf_not_applicable' AND B.correspondents_id NOT LIKE 'unnamed_person_viaf_not_applicable')) AND L. recipient_id IN (SELECT X.correspondents_id FROM budé_cdb_v1.correspondents AS X WHERE X.correspondents_id IN (Select E.correspondents_id from era_cdb_v3.correspondents AS E, budé_cdb_v1.correspondents AS B WHERE E.correspondents_id = B.correspondents_id AND E.correspondents_id NOT LIKE 'unnamed_person_viaf_not_applicable' AND B.correspondents_id NOT LIKE 'unnamed_person_viaf_not_applicable'))
