SELECT * FROM (SELECT * FROM era_cdb_v3.locations UNION ALL (SELECT * FROM budé_cdb_v1.locations As B WHERE B.locations_id NOT IN (SELECT E.locations_id FROM era_cdb_v3.locations AS E))) AS LOC where LOC.locations_id IN (SELECT DISTINCT source_loc_id FROM (SELECT * from era_cdb_v3.letters WHERE send_date_year1 = '1499' UNION ALL (Select * from budé_cdb_v1.letters WHERE letters_id NOT LIKE '%_cwe_%' AND send_date_year1 = '1499')) AS L WHERE L.sender_id IN (SELECT X.correspondents_id FROM era_cdb_v3.correspondents AS X WHERE X.correspondents_id IN (Select B.correspondents_id from budé_cdb_v1.correspondents As B, era_cdb_v3.correspondents AS E WHERE B.correspondents_id = E.correspondents_id AND B.correspondents_id NOT LIKE 'unnamed_person_viaf_not_applicable' AND E.correspondents_id NOT LIKE 'unnamed_person_viaf_not_applicable')) AND L. recipient_id IN (SELECT X.correspondents_id FROM era_cdb_v3.correspondents AS X WHERE X.correspondents_id IN (Select B.correspondents_id from budé_cdb_v1.correspondents As B, era_cdb_v3.correspondents AS E WHERE B.correspondents_id = E.correspondents_id AND B.correspondents_id NOT LIKE 'unnamed_person_viaf_not_applicable' AND E.correspondents_id NOT LIKE 'unnamed_person_viaf_not_applicable')))
