SELECT COUNT(*) AS 'Number of Correspondents that both wrote to and received letters from Pirckheimer' FROM correspondents WHERE correspondents.correspondents_id IN (SELECT DISTINCT recipient_id FROM letters WHERE recipient_id != 'pirckheimer_willibald_viaf_27173507') AND correspondents.correspondents_id IN (SELECT sender_id FROM letters WHERE sender_id != 'pirckheimer_willibald_viaf_27173507')
