SELECT COUNT(*) AS 'Number of correspondents who both wrote letters to Pirckheimer and received letters from him' FROM correspondents WHERE correspondents.correspondents_id IN (SELECT DISTINCT recipient_id FROM letters WHERE recipient_id != 'pirckheimer_willibald_viaf_27173507') AND correspondents.correspondents_id IN (SELECT sender_id FROM letters WHERE sender_id != 'pirckheimer_willibald_viaf_27173507')
