SELECT y.locations_name_modern AS 'Location Name', x.locations_lat AS 'Latitude', x.locations_lng AS 'Longitude', x.count AS 'Number of correspondents Pirckheimer writes to', y.count AS 'Number of letters written by Pirckheimer', Y.COUNT/X.COUNT AS 'Average Number of Letters'  FROM (SELECT locations_name_modern, locations_lat, locations_lng, COUNT(DISTINCT recipient_id) AS COUNT FROM letters JOIN wpirck_cdb_v1.locations ON locations.locations_id = letters.target_loc_id WHERE sender_id = 'pirckheimer_willibald_viaf_27173507' GROUP BY target_loc_id ORDER BY COUNT DESC) AS X INNER JOIN (SELECT locations.locations_name_modern, COUNT(letters.target_loc_id) AS COUNT FROM letters JOIN locations ON locations.locations_id = letters.target_loc_id WHERE letters_id NOT REGEXP '[0-7]ck2|ck3|ck4|ck5|ck6|ck7|ck8]' AND sender_id = 'pirckheimer_willibald_viaf_27173507' GROUP BY target_loc_id ORDER BY COUNT DESC) AS Y on X.locations_name_modern = Y.locations_name_modern
